CSV_FILE = "sustainable_waste_management_dataset_2024.csv"
df = pd.read_csv(CSV_FILE)
if st.button("View DataBase"):
    st.dataframe(df)
st.button("Close DataBase",type= "primary")
selected_features = ['recyclable_kg','organic_kg','temp_c','rain_mm','collection_capacity_kg']
X = df[selected_features]
y = df['waste_kg']

df_combined = pd.concat([X, y], axis=1)
df_combined.dropna(inplace=True)

X = df_combined[selected_features]
y = df_combined['waste_kg']


X_train, X_test, Y_train, Y_test = train_test_split(X, y, test_size=0.2, random_state=30)

model = LinearRegression()

model.fit(X_train, Y_train)

Y_pred = model.predict(X_test)

print("MSE: ", mean_squared_error(Y_test, Y_pred))
print("R squared: ", r2_score(Y_test, Y_pred))


plt.figure(figsize=(10, 6))
plt.scatter(Y_test, Y_pred, alpha=0.7)
plt.plot([y.min(), y.max()], [y.min(), y.max()], '--', color='red', lw=2, label='Perfect Prediction Line')
plt.xlabel('Actual Waste_kg (Y_test)')
plt.ylabel('Predicted Waste_kg (Y_pred)')
plt.title('Predicted Waste vs. Actual Waste')
plt.legend()
plt.grid(True)
plt.show()

fig, ax = plt.subplots()
ax.scatter(Y_test, Y_pred, alpha=0.7)
ax.plot([y.min(), y.max()], [y.min(), y.max()], '--', color='red', lw=2, label='Perfect Prediction Line')
ax.set_xlabel('Actual Waste_kg (Y_test)')
ax.set_ylabel('Predicted Waste_kg')
ax.set_title('Predicted Waste vs. Actual Waste')
ax.legend()
ax.grid(True)
if st.button("View Graph"):
    st.pyplot(fig)
st.button("Close Graph",type= "primary")
